// 리액트 패키지를 불러옵니다.
import React, { useEffect, useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import styled from "styled-components";

// redux hook을 불러옵니다.
import { useDispatch, useSelector } from "react-redux";
import { createBucket, deleteBucket, loadBucket } from "./redux/modules/bucket";

import { addDoc, collection, getDocs } from "firebase/firestore";
import { db } from "./firebase";

const BucketList = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();

    // 버킷리스트를 리덕스 훅으로 가져오기
    const bucket_list = useSelector((state) => state.bucket.list);

    //const initialState = ["영화관 가기", "매일 책읽기", "수영 배우기"];
    //const [list, setList] = useState(initialState);
    // const addTodo = () => {
    //     setList([...list, todoInput.current.value]);
    //     console.log(list)
    // }

    useEffect(() => {
        let bucket_data = [];
        const querySnapshot = getDocs(collection(db, "bucket_list"));

        querySnapshot.then(async function (data) {
            await data.forEach((doc) => {
                if (doc.exists) {
                    bucket_data = [...bucket_data, { ...doc.data() }];
                }
                console.log(bucket_data);
                dispatch(loadBucket(bucket_data));
            });
        });

        return () => {
            console.log("컴포넌트가 화면에서 사라짐");
        };
    }, []);

    //console.log("나오나", bucket_data);

    const todoInput = useRef();

    const addTodoDispatch = () => {
        //dispatch(createBucket(todoInput.current.value));
        addDoc(collection(db, "bucket_list"), { text: todoInput.current.value, completed: false });
        todoInput.current.value = "";
    };

    return (
        <>
            <ListStyle>
                {console.log(bucket_data)}
                {bucket_list.map((list, index) => {
                    return (
                        <ItemStyle
                            key={index}
                            className="list_item"
                            color={list.completed ? "orange" : "aliceblue"}
                            onClick={() => {
                                navigate(`detail?${index}`);
                            }}
                        >
                            {list.text}
                        </ItemStyle>
                    );
                })}
            </ListStyle>
            <Input>
                <input type="text" ref={todoInput} />
                <button onClick={addTodoDispatch}>추가하기</button>
            </Input>
        </>
    );
};

const ListStyle = styled.div`
    display: flex;
    flex-direction: column;
    height: 100%;
    overflow-x: hidden;
    overflow-y: auto;
`;

const ItemStyle = styled.div`
    padding: 16px;
    margin: 8px;
    background-color: ${(props) => props.color};
`;

const Input = styled.div`
    max-width: 350px;
    min-height: 10vh;
    background-color: #fff;
    padding: 16px;
    margin: 20px auto;
    border-radius: 5px;
    border: 1px solid #ddd;
`;

export default BucketList;
