// 리액트 패키지를 불러옵니다.
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
// redux hook을 불러옵니다.
import { useDispatch, useSelector } from "react-redux";
import { deleteBucket, doneBucket } from "./redux/modules/bucket";
// import { doc, updateDoc } from "firebase/firestore";
// import { db } from "./firebase";

const Detail = (props) => {
    // 1. useLocation 훅 취득
    const location = useLocation();
    const dispatch = useDispatch();
    const navigate = useNavigate();
    let dataIdx = location.search.substring(1);

    // 스토어에서 상태값 가져오기
    const bucket_list = useSelector((state) => state.bucket.list);

    const delList = () => {
        dispatch(deleteBucket(dataIdx));
        navigate(-1);
    };

    const todoDone = () => {
        //updateDoc(doc(db, "bucket_list", bucket_list.id), { completed: true });
        dispatch(doneBucket(dataIdx));
        navigate(-1);
    };

    return (
        <>
            <h1>{dataIdx} 번째 상세 페이지입니다! </h1>
            <div>{bucket_list[dataIdx].text}</div>
            <button onClick={todoDone}>완료하기</button>
            <button onClick={delList}>삭제하기</button>
        </>
    );
};

export default Detail;
