// 리액트 패키지를 불러옵니다.
import React, { useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import styled from "styled-components";

// redux hook을 불러옵니다.
import {useDispatch, useSelector} from 'react-redux';
import { createBucket, deleteBucket } from "./redux/modules/bucket";

const BucketList = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  // 버킷리스트를 리덕스 훅으로 가져오기
  const bucket_list = useSelector(state => state.bucket.list);

  const initialState = ["영화관 가기", "매일 책읽기", "수영 배우기"];

  const [list, setList] = useState(initialState);
  const todoInput = useRef();

  const addTodo = () => {
      setList([...list, todoInput.current.value]);
      console.log(list)
  }

  const addTodoDispatch = () => {
    dispatch(createBucket(todoInput.current.value))
  }

  const delList = (index) => {
    dispatch(deleteBucket(index))
  }

  return (
    <>
        <ListStyle>
        {bucket_list.map((list, index) => {
            return (
            <ItemStyle key={index} className="list_item" onClick={()=>{ navigate(`detail?${index}`) }}>
                {list}
            </ItemStyle>
            );
        })}
        </ListStyle>
        <div>
            <input type="text" ref={todoInput} />
            <button onClick={addTodoDispatch}>추가하기</button>
        </div>
    </>
  );
};

const ListStyle = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow-x: hidden;
  overflow-y: auto;
`;

const ItemStyle = styled.div`
  padding: 16px;
  margin: 8px;
  background-color: aliceblue;
`;

export default BucketList;
