const LOAD = 'bucket/LOAD';
const CREATE = 'bucket/CREATE';
const DELETE = 'bucket/DELETE';

const initialState = {
    list: ["영화관 가기 redux", "매일 책읽기 redux", "수영 배우기 redux"],
  };

  export const loadBucket = (bucket) => {
    return { type: LOAD, bucket };
}

export const createBucket = (bucket) => {
    return {type: CREATE, bucket};
}

export const deleteBucket = (bucket) => {
    return {type: DELETE, bucket};
}

export default function reducer(state = initialState, action = {}) {
  switch (action.type) {
    // do reducer stuff
    case "bucket/LOAD":
      return state;

    case "bucket/CREATE":
      const add_bucket_list = [...state.list, action.bucket];
      return { list: add_bucket_list };

    case "bucket/DELETE":
        console.log(action.bucket)
        const del_bucket_list = state.list.filter((item, idx)=> {
            console.log('idx',idx)
            if(idx != action.bucket){
                return item
            }
        });
        console.log(del_bucket_list)
        return {list:del_bucket_list};
    default:
      return state;
  }
}
