const LOAD = "bucket/LOAD";
const CREATE = "bucket/CREATE";
const DELETE = "bucket/DELETE";
const DONE = "bucket/DONE";

const initialState = {
    //list: ["영화관 가기 redux", "매일 책읽기 redux", "수영 배우기 redux"],
    list: [
        { text: "영화관 가기", completed: false },
        { text: "매일 책읽기", completed: false },
        { text: "수영 배우기", completed: false },
    ],
};

export const loadBucket = (bucket) => {
    return { type: LOAD, bucket };
};

export const createBucket = (bucket) => {
    return { type: CREATE, bucket };
};

export const deleteBucket = (bucket) => {
    return { type: DELETE, bucket };
};

export const doneBucket = (number) => {
    return { type: DONE, number };
};

export default function reducer(state = initialState, action = {}) {
    switch (action.type) {
        // do reducer stuff
        case "bucket/LOAD":
            return state;

        case "bucket/CREATE":
            const add_bucket_list = [...state.list, { text: action.bucket, completed: false }];
            return { list: add_bucket_list };

        case "bucket/DELETE":
            const del_bucket_list = state.list.filter((item, idx) => idx != action.bucket);
            return { list: del_bucket_list };

        case "bucket/DONE":
            const done_bucket_list = state.list.map((item, idx) => {
                if (idx == action.number) {
                    return { ...item, completed: true };
                }
                return item;
            });
            return { list: done_bucket_list };
        default:
            return state;
    }
}
