import React from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";

const Score = (props) => {
    const navigate = useNavigate();
    const name = useSelector((state) => state.quiz.name);
    const score_texts = useSelector((state) => state.quiz.score_texts);
    const answers = useSelector((state) => state.quiz.answers);

    // 정답만 걸러내기
    let correct = answers.filter((answer) => {
        return answer;
    });

    // 점수 계산하기
    let score = (correct.length / answers.length) * 100;

    // 점수별로 텍스트를 띄워줄 준비!
    let score_text = "";

    // Object.keys는 딕셔너리의 키값을 배열로 만들어주는 친구예요!
    Object.keys(score_texts).map((s, idx) => {
        // 첫번째 텍스트 넣어주기
        if (idx === 0) {
            score_text = score_texts[s];
        }
        // 실제 점수와 기준 점수(키로 넣었던 점수) 비교해서 텍스트를 넣자!
        score_text = parseInt(s) <= score ? score_texts[s] : score_text;
    });

    return (
        <ScoreContainer>
            <Text>
                <span>{name}</span>
                퀴즈에 <br />
                대한 내 점수는?
            </Text>
            <MyScore>
                <span>100</span>점<p>{score_text}</p>
            </MyScore>

            {/* <Button>다시 하기</Button> */}
            <Button onClick={() => navigate("/message")} outlined={false}>
                {name}에게 한마디
            </Button>
        </ScoreContainer>
    );
};

const ScoreContainer = styled.div`
    display: flex;
    width: 100vw;
    height: 100vh;
    overflow: hidden;
    padding: 16px;
    box-sizing: border-box;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Text = styled.h1`
    font-size: 1.5em;
    margin: 0px;
    line-height: 1.4;
    & span {
        background-color: #fef5d4;
        padding: 5px 10px;
        border-radius: 30px;
    }
`;

const MyScore = styled.div`
    & span {
        border-radius: 30px;
        padding: 5px 10px;
        background-color: #fef5d4;
    }
    font-weight: 600;
    font-size: 2em;
    margin: 24px;

    & > p {
        margin: 24px 0px;
        font-size: 16px;
        font-weight: 400;
    }
`;
const Button = styled.button`
    padding: 8px 24px;
    background-color: ${(props) => (props.outlined ? "#ffffff" : "#dadafc")};
    border-radius: 30px;
    margin: 8px;
    border: 1px solid #dadafc;
    width: 80vw;
`;

export default Score;
