import React from 'react';
// Route를 먼저 불러와줍니다.
import { Link, Route, Routes } from "react-router-dom";
import { withRouter } from "react-router";

// 세부 페이지가 되어줄 컴포넌트들도 불러와주고요!
import Home from "./router/Home";
import Cat from "./router/Cat";
import Dog from "./router/Dog";

class App extends React.Component {

  constructor(props){
    super(props);
    this.state={};
  }
  componentDidMount(){
    console.log(this.props);
  }



  render(){
    return (
      <div className="App">
        <div>
          <Link to="/" style={{marginRight:"10px"}}>Home으로 가기</Link>
          <Link to="/cat" style={{marginRight:"10px"}}>Cat으로 가기</Link>
          <Link to="/dog">Dog으로 가기</Link>
        </div>

        <hr />
        {/* 실제로 연결해볼까요! */}
        <Routes>
          <Route path="/" element={<Home />}  />
          <Route path="/cat" element={<Cat />}></Route>
          <Route path="/cat/:name" element={<Cat />}></Route>
          <Route path="/dog" element={<Dog />} />
        </Routes>

      console.log(this.props.history)

        <button onClick={() => {
          // props에 있는 history를 사용합니다.
          // push([이동할 주소])는 페이지를 이동시켜 줍니다.
          this.props.history.push('/cat');
        }}>
          /cat으로 가기
        </button>
        <button onClick={()=>{
          // goBack()은 뒤로가기 예요.
          this.props.history.goBack();
        }}>뒤로가기
        </button>
      </div>
    );
  }
}

export default App;
