import React from "react";
import styled from "styled-components";

const Score = (props) => {
    console.log(props);

    const name = useSelector((state) => state.quiz.name);
    const score_texts = useSelector((state) => state.quiz.score_texts);

    const answers = useSelector((state) => state.quiz.answers);

    // 정답만 걸러내기
    let correct = answers.filter((answer) => {
        return answer;
    });

    // 점수 계산하기
    let score = (correct.length / answers.length) * 100;

    // 점수별로 텍스트를 띄워줄 준비!
    let score_text = "";

    return (
        <ScoreContainer>
            <Text>
                <span>{props.name}</span>
                퀴즈에 <br />
                대한 내 점수는?
            </Text>
            <MyScore>
                <span>100</span>점<p>{props.scoreMsg}</p>
            </MyScore>

            {/* <Button>다시 하기</Button> */}
            <Button outlined>랭킹보기</Button>
        </ScoreContainer>
    );
};

const ScoreContainer = styled.div`
    display: flex;
    width: 100vw;
    height: 100vh;
    overflow: hidden;
    padding: 16px;
    box-sizing: border-box;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Text = styled.h1`
    font-size: 1.5em;
    margin: 0px;
    line-height: 1.4;
    & span {
        background-color: #fef5d4;
        padding: 5px 10px;
        border-radius: 30px;
    }
`;

const MyScore = styled.div`
    & span {
        border-radius: 30px;
        padding: 5px 10px;
        background-color: #fef5d4;
    }
    font-weight: 600;
    font-size: 2em;
    margin: 24px;

    & > p {
        margin: 24px 0px;
        font-size: 16px;
        font-weight: 400;
    }
`;
const Button = styled.button`
    padding: 8px 24px;
    background-color: ${(props) => (props.outlined ? "#ffffff" : "#dadafc")};
    border-radius: 30px;
    margin: 8px;
    border: 1px solid #dadafc;
    width: 80vw;
`;

export default Score;
