import logo from './logo.svg';
import './App.css';
import React from "react";

// 일단 스타일 파일은 주석처리하고 그 아래에서 불러올거예요!
// import "./style.css";
import "./scss_ex.scss";
import styled from 'styled-components';
import BucketList from './BucketList';
import Start from "./Start";
import Quiz from './Quiz';
import Score from './Score';

class App extends React.Component{
  constructor(props){
    super(props);

    this.state = {
      name: "스파르타 코딩 클럽",
      page: "start",
      scoreMsg: "이 정도면 아주 친한 친구 사이! 앞으로도 더 친하게 지내요! :)",
      list: [
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
        { question: "르탄이는 2살이다.", answer: "O" },
      ],
      ranking: [
        { rank: 1, name: "임민영", message: "안녕 르탄아!" },
        { rank: 1, name: "임민영", message: "안녕 르탄아!" },
        { rank: 1, name: "임민영", message: "안녕 르탄아!" },
        { rank: 1, name: "임민영", message: "안녕 르탄아!" },
        { rank: 1, name: "임민영", message: "안녕 르탄아!" },
        { rank: 1, name: "임민영", message: "안녕 르탄아!" },
        { rank: 1, name: "임민영", message: "안녕 르탄아!" },
      ],
    };
  }

  render () {
    return (
    <div className="App">

      {this.state.page === "quiz" && (<Quiz list={this.state.list} />)}
      {this.state.page === "start" && (<Start list={this.state.name} />)}
      {this.state.page === "score" && (<Score list={this.state.name, this.state.scoreMsg} />)}


      {/* <Start name={this.state.name}/> */}
      {/* <Mystyled bgColor={"red"} /> */}
      {/* <BucketList /> */}
    </div>
    )
  }
}

const Mystyled = styled.div`
  width: 50vw;
  min-height: 150px;
  padding: 10px;
  border-radius: 15px;
  color: #fff;
  &:hover{
    background-color: #ddd;
  }
  background-color: ${(props) => (props.bgColor ? "red" : "purple")};
`

export default App;
