import React, { useState } from "react";

import Start from "./components/Start";
import Quiz from "./components/Quiz";
import Score from "./components/Score";
// 리덕스 스토어와 연결하기 위해 connect라는 친구를 호출할게요!
import { connect } from "react-redux";

const App = () => {
    // state에 필요한 데이터를 넣어줘요!
    const initialState = {
        name: "스파르타 코딩 클럽",
        page: "quiz",
        scoreMsg: "이 정도면 아주 친한 친구 사이! 앞으로도 더 친하게 지내요! :)",
        list: [
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
            { question: "르탄이는 2살이다.", answer: "O" },
        ],
        ranking: [
            { rank: 1, name: "임민영", message: "안녕 르탄아!" },
            { rank: 1, name: "임민영", message: "안녕 르탄아!" },
            { rank: 1, name: "임민영", message: "안녕 르탄아!" },
            { rank: 1, name: "임민영", message: "안녕 르탄아!" },
            { rank: 1, name: "임민영", message: "안녕 르탄아!" },
            { rank: 1, name: "임민영", message: "안녕 르탄아!" },
            { rank: 1, name: "임민영", message: "안녕 르탄아!" },
        ],
    };

    const [state, setState] = useState(initialState);

    return (
        <div className="App">
            {/* 조건부 랜더링을 합니다 / state의 page를 바꿔가면서 확인해봐요! */}
            {state.page === "quiz" && <Quiz list={state.list} />}
            {state.page === "start" && <Start name={state.name} />}
            {state.page === "score" && <Score name={state.name} scoreMsg={state.scoreMsg} />}
            {state.page === "message" && <Start name={state.name} />}
            {state.page === "ranking" && <Start name={state.name} />}
        </div>
    );
};

export default App;
